package reportengine

import (
	"github.com/signintech/gopdf"
	"math/rand"
	"strconv"
	"time"
)

var letterRunes = []rune("abcdefghijklmnopqrstuvwxyzABCDEFGHIJKLMNOPQRSTUVWXYZ ")

func init() {
	rand.Seed(time.Now().UnixNano())
}

func getGrid() Component {
	m := make([][]Component, 3)
	m[0] = make([]Component, 1)
	m[1] = make([]Component, 3)
	m[2] = make([]Component, 1)
	m[0][0] = getCellTextArea()
	m[1][0] = getCellTextArea()
	m[1][1] = getCellTextArea()
	m[1][2] = getCellTextArea()
	m[2][0] = getCellTextArea()
	return NewGrid(m, NewRectangle(gopdf.AllBorders, Solid, 1.1, White(), Red(), true), NewMargin(5),
		gopdf.Left, gopdf.Top)
}

func getGridWithImages(dpi float64) Component {
	m := make([][]Component, 2)
	m[0] = make([]Component, 6)
	m[1] = make([]Component, 6)

	m[0][0] = NewCellTextArea(gopdf.Center, gopdf.Middle, "JPEG", false, "Arial-Regular", 20,
		Black(), NewMargin(2), NewRectangle(gopdf.AllBorders, Solid, 1, White(), Black(), true))
	m[1][0] = NewCellImage(gopdf.Center, gopdf.Middle, NewMargin(1), NewRectangle(gopdf.AllBorders, Solid,
		1.0, White(), Black(), true), imgBase64JPEG, dpi)

	m[0][1] = NewCellTextArea(gopdf.Center, gopdf.Middle, "PNG", false, "Arial-Regular", 20,
		Black(), NewMargin(2), NewRectangle(gopdf.AllBorders, Solid, 1, White(), Black(), true))
	m[1][1] = NewCellImage(gopdf.Center, gopdf.Middle, NewMargin(1), NewRectangle(gopdf.AllBorders, Solid,
		1.0, White(), Black(), true), imgBase64PNG, dpi)

	m[0][2] = NewCellTextArea(gopdf.Center, gopdf.Middle, "SVG", false, "Arial-Regular", 20,
		Black(), NewMargin(2), NewRectangle(gopdf.AllBorders, Solid, 1, White(), Black(), true))
	m[1][2] = NewCellImage(gopdf.Center, gopdf.Middle, NewMargin(1), NewRectangle(gopdf.AllBorders, Solid,
		1.0, White(), Black(), true), imgBase64SVG, dpi)

	m[0][3] = NewCellTextArea(gopdf.Center, gopdf.Middle, "BMP", false, "Arial-Regular", 20,
		Black(), NewMargin(2), NewRectangle(gopdf.AllBorders, Solid, 1, White(), Black(), true))
	m[1][3] = NewCellImage(gopdf.Center, gopdf.Middle, NewMargin(1), NewRectangle(gopdf.AllBorders, Solid,
		1.0, White(), Black(), true), imgBase64BMP, dpi)

	m[0][4] = NewCellTextArea(gopdf.Center, gopdf.Middle, "GIF", false, "Arial-Regular", 20,
		Black(), NewMargin(2), NewRectangle(gopdf.AllBorders, Solid, 1, White(), Black(), true))
	m[1][4] = NewCellImage(gopdf.Center, gopdf.Middle, NewMargin(1), NewRectangle(gopdf.AllBorders, Solid,
		1.0, White(), Black(), true), imgBase64GIF, dpi)

	m[0][5] = NewCellTextArea(gopdf.Center, gopdf.Middle, "TIFF", false, "Arial-Regular", 20,
		Black(), NewMargin(2), NewRectangle(gopdf.AllBorders, Solid, 1, White(), Black(), true))
	m[1][5] = NewCellImage(gopdf.Center, gopdf.Middle, NewMargin(1), NewRectangle(gopdf.AllBorders, Solid,
		1.0, White(), Black(), true), imgBase64TIFF, dpi)

	return NewGrid(m, NewRectangle(gopdf.AllBorders, Solid, 1, White(), Red(), true), NewMargin(5),
		gopdf.Center, gopdf.Middle)
}

func getTable(nRow, nCol int) Component {
	m := make([][]Component, nRow)
	for i := 0; i < nRow; i++ {
		m[i] = make([]Component, nCol)
	}
	for j := 0; j < nCol; j++ {
		m[0][j] = NewCellTextArea(gopdf.Center, gopdf.Middle, "Header-"+strconv.Itoa(j), false,
			"Rubik-Regular", 14, Color{0, 255, 255}, NewMargin(2),
			NewRectangle(gopdf.AllBorders, Solid, 1.1, Yellow(), Red(), true))
	}
	for i := 1; i < nRow; i++ {
		m[i][0] = getCellTextAreaStr("#" + strconv.Itoa(i))
		for j := 1; j < nCol; j++ {
			m[i][j] = getRandomCellTextArea()
		}
	}
	return NewGrid(m, NewRectangle(gopdf.AllBorders, Solid, 1.1, White(), Black(), true), NewMargin(5),
		gopdf.Center, gopdf.Middle)
}

func getCellTextAreaStr(str string) Component {
	return NewCellTextArea(gopdf.Center, gopdf.Middle, str,
		true, "ArchitectsDaughter-Regular", 14, Color{0, 255, 255},
		NewMargin(2), NewRectangle(gopdf.AllBorders, Solid, 1.1, Yellow(), Red(), true))
}

func getCellTextArea() Component {
	return NewCellTextArea(gopdf.Center, gopdf.Middle, "Questo è un cane blu i{0xF0A43;#0000FF} e questo è un gatto i{0xF011B;#FF0000} rosso.",
		true, "ArchitectsDaughter-Regular", 14, Color{0, 255, 255},
		NewMargin(2), NewRectangle(gopdf.AllBorders, Solid, 1.1, Yellow(), Red(), true))
}

func getRandomCellTextArea() Component {
	return NewCellTextArea(gopdf.Center, gopdf.Middle, randStringRunes(8),
		true, "ArchitectsDaughter-Regular", 12, Color{0, 255, 255},
		NewMargin(2), NewRectangle(gopdf.AllBorders, Solid, 1.1, Yellow(), Red(), true))
}

func randStringRunes(n int) string {
	b := make([]rune, n)
	for i := range b {
		b[i] = letterRunes[rand.Intn(len(letterRunes))]
	}
	return string(b)
}

const imgBase64JPEG = "data:image/jpeg;base64,"
const imgBase64PNG = "data:image/png;base64,"
const imgBase64TIFF = "data:image/tiff;base64,"
const imgBase64GIF = "data:image/gif;base64,"
const imgBase64BMP = "data:image/bmp;base64,"
const imgBase64SVG = "data:image/svg+xml;base64,"
